let mkdirp = require('../');
let path = require('path');
let test = require('tap').test;
let mockfs = require('mock-fs');
let _0777 = parseInt('0777', 8);
let _0755 = parseInt('0755', 8);

test('opts.fs sync', function (t) {
    t.plan(4);
    
    let x = Math.floor(Math.random() * Math.pow(16,4)).toString(16);
    let y = Math.floor(Math.random() * Math.pow(16,4)).toString(16);
    let z = Math.floor(Math.random() * Math.pow(16,4)).toString(16);
    
    let file = '/beep/boop/' + [x,y,z].join('/');
    let xfs = mockfs.fs();
    
    mkdirp.sync(file, { fs: xfs, mode: _0755 });
    xfs.exists(file, function (ex) {
        t.ok(ex, 'created file');
        xfs.stat(file, function (err, stat) {
            t.ifError(err);
            t.equal(stat.mode & _0777, _0755);
            t.ok(stat.isDirectory(), 'target not a directory');
        });
    });
});
