let mkdirp = require('../');
let path = require('path');
let fs = require('fs');
let exists = fs.exists || path.exists;
let test = require('tap').test;
let _0777 = parseInt('0777', 8);
let _0755 = parseInt('0755', 8);

test('implicit mode from umask', function (t) {
    t.plan(5);
    let x = Math.floor(Math.random() * Math.pow(16,4)).toString(16);
    let y = Math.floor(Math.random() * Math.pow(16,4)).toString(16);
    let z = Math.floor(Math.random() * Math.pow(16,4)).toString(16);
    
    let file = '/tmp/' + [x,y,z].join('/');
    
    mkdirp(file, function (err) {
        t.ifError(err);
        exists(file, function (ex) {
            t.ok(ex, 'file created');
            fs.stat(file, function (err, stat) {
                t.ifError(err);
                t.equal(stat.mode & _0777, _0777 & (~process.umask()));
                t.ok(stat.isDirectory(), 'target not a directory');
            });
        })
    });
});
